import config from './config';

Array.matrix = function(row, col, callback) {
    const matirx = [];
    let count = 0;
    for (let i = 0; i < row; i++) {
        const rowArr = [];
        for (let j = 0; j < col; j++) {
            rowArr.push(callback(i, j, count));
            count += 1;
        }
        matirx.push(rowArr);
    }
    return matirx;
}

class CreateScreen {
    constructor(canvasDom, options = {}) {
        this.canvas = canvasDom;
        this.ctx = canvasDom.getContext('2d');
        this.screenWidth = window.screen.width;
        this.screenHeight = window.screen.height;
        const { HORI_SCREEN_NUMB: col, VERT_SCREEN_NUM: row } = config;
        this.screenMatrix = Array.matrix(row, col, (i, j, count) => ({
            n: count,
            originX: j * window.screen.width,
            originY: i * window.screen.height,
            app: []
        }));
        this.config = Object.assign({}, config, options);
        for (let i = 0; i < (col * row); i++) {
            this.setAppInScreen(i);
        }
        this.init();
    }

    init() {
        this.setCanvasSize();
        this.drawScreen();
    }

    show(num) {
        const { originX, originY } = this.findScreen(num);
        const { screenHeight, screenWidth } = this;
        const centerX = originX + screenWidth / 2;
        const centerY = originY + screenHeight / 2;
        this.canvas.style.transform = `translate3d(-${originX}px, -${originY}px, 0) scale(1)`;
        this.canvas.style.transformOrigin = `${centerX}px ${centerY}px`;
    }

    setCanvasSize() {
        const { screenWidth, screenHeight, config } = this;
        const { HORI_SCREEN_NUMB, VERT_SCREEN_NUM } = config;
        this.canvas.width = screenWidth * HORI_SCREEN_NUMB;
        this.canvas.height = screenHeight * VERT_SCREEN_NUM;
    }

    async drawScreen() {
        await this.drawBg();

        const {HORI_SCREEN_NUMB: x, VERT_SCREEN_NUM: y} = this.config;
        const screenCount = x * y;
        Array(screenCount).fill(1).forEach((_, i) => {
            this.drawAppIcon(i);
        })
    }

    drawBg() {
        const { ctx, canvas, screenWidth, screenHeight } = this;
        return this.drawImg(require('./images/xxb.jpg'), screenWidth * 2, screenHeight, screenWidth, screenHeight)
    }

    drawImg(img, x, y, w, h) {
        return new Promise((resolve, reject) => {
            let image = new Image();
            image.src = img;
            image.onload = () => {
                image.width = w;
                image.height = h;
                this.ctx.drawImage(image, x, y, w, h);
                image = null;
                resolve();
            }
        })
    }

    getScreenColRow(num) {
        const obj = {
            col: 0,
            row: 0
        }
        this.screenMatrix.forEach((el, row) => {
            el.forEach((screen, col) => {
                if (screen.n == num) {
                    obj.col = col;
                    obj.row = row;
                }
            })
        });
        return obj;
    }

    getAppData() {
        const { APP_ICON_ROW: row, APP_ICON_COL: col } = this.config;
        const appData = Array.matrix(row, col, (x, y) => ({
            name: '微信',
            icon: require('./images/wx.png')
        }))
        return appData;
    }

    findScreen(num) {
        let obj = null;
        this.screenMatrix.forEach((el, row) => {
            el.forEach((screen, col) => {
                if (screen.n == num) {
                    obj = screen;
                }
            })
        });
        return obj;
    }

    setAppInScreen(i) {
        const screen = this.findScreen(i);
        screen.app = this.getAppData();
    }

    drawAppIcon(num = 0) {
        const { width, height } = this.canvas;
        const { APP_ICON_ROW, APP_ICON_COL } = this.config;
        const { screenWidth, screenHeight } = this;
        const {
            app,
            originX,
            originY
        } = this.findScreen(num);
        const iconSize = 70;
        const padBottom = 100;
        const eachWidth = screenWidth / APP_ICON_COL;
        const eachHeight = (screenHeight - padBottom) / APP_ICON_ROW;
        const halfWidth = eachWidth / 2 - iconSize / 2;
        const halfHeight = eachHeight / 2 - iconSize / 2;
        app.forEach((rows, i) => {
            rows.forEach((data, k) => {
                const x = originX + halfWidth + eachWidth * k;
                const y = originY + halfHeight + eachHeight * i;
                data && this.drawImg(data.icon, x, y, iconSize, iconSize);
            })
        })
    }
}

export default CreateScreen;
